package Operators;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=11;
		int b=2;
		
		System.out.println(a+b); 
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b); 
		System.out.println(a%b); //remainder of division
		System.out.println(++a); //Pre-Increment: Value is incremented first and then the result is computed
		System.out.println(a++); //Post-Increment: Value is first used for computing the result and then incremented.
		System.out.println(a--); //Post-decrement: Value is first used for computing the result and then decremented.
		System.out.println(--a); //Pre-decrement (Ex: --a): Value is decremented first and then the result is computed


	}

}
