package Operators;

public class ConditionalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20,b;
		
		b=(a==1)?30:40; //if epr1 is true then expression evaluates after question mark (?) else evaluates after colon (:). See the below example.
		System.out.println(b);
		b=(a==20)?30:40;
		System.out.println(b);

	}

}
