package Strings;
//Write a java program to combine two strings
//Write a java program to reverse a String.compare two strings
public class combine_reverse_compare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1="chandana";
		String s2="potllapalli";
		
		StringBuffer sb=new StringBuffer("Chandana");
		System.out.println(s1.concat(" "+s2));
		System.out.println(sb.reverse());
		System.out.println(s1.compareTo(s2));


	}

}
